void connect_callback(struct mosquitto *mosq, void *obj, int result)
{
	printf("成功連上Broker, rc=%d\n", result);
}

//todo 當從Broker收到訊息時, 要處理的區塊
void message_callback(struct mosquitto *mosq, void *obj, const struct mosquitto_message *message) //message_callback(結構指標變數, 接收接收實例物件, 接收傳來的訊息), 參數3會在此函數完成時, 會釋放message, client端如果需要的話要存起來要另外存
{
	bool match = 0;
	char *s,*p;
	int gpio_num;
	volatile int value;
    char cmd[100];

	printf("收到來自Broker的Topic:'%s' 內容:'%.*s'\n", message->topic, message->payloadlen, (char*) message->payload);  
	s=message->payload; 

	if((p=strchr(s,'/'))!=NULL); 
	{    
		*p='\0';
		gpio_num=atoi(s); 
		value=atoi(p+1);
		printf("準備將Broker傳的訊息: %d, %d 傳至servo motor\n",gpio_num,value);
		printf("0是開");


        printf("test value=%d \n", value);	
        if (value==0) {	
            // Turn it on
			clear_LCD();
            print_Line(0, "AIoT Parking Lot");
			print_Line(1, " NTD$20 / Hours ");
			print_Line(2, " Open the Fence ");
			print_Line(3, s);
			
			printf("car coming");
            for (int i = 0; i <= 20; i+=5) {
            bcm2835_pwm_set_data(0,i);  //set pulse width
			
			
            // draw_LCD(BMP_Logo_name2);
            bcm2835_delay(50);
            } 
        } else if (value==1) {	
        // turn it off
			clear_LCD();
        	print_Line(0, "AIoT Parking Lot");
			print_Line(1, " NTD$20 / Hours ");
            print_Line(2, "Close the Fence ");
			print_Line(3, " Enjoy your day ");

			printf("car left");
            for (int i =20; i >= 0; i-=5) {
            bcm2835_pwm_set_data(0,i);    //set pulse width
			
			
			// draw_LCD(BMP_Logo_name1);
            bcm2835_delay(50);
            }
	}
		
	}

	mosquitto_topic_matches_sub("sensor/gpio", message->topic, &match);  
	if (match) 
	{
		printf("got message for GPIO topic\n");
	}
}

//! mqtt初始化
int mqtt_init(void)
{
	uint8_t reconnect = true;
	char clientid[24];
	struct mosquitto *mosq;
	int rc = 0;

	signal(SIGINT, handle_signal);  //todo Linux設計技巧, 中斷處理 SIGINT就是Ctrl + C
	signal(SIGTERM, handle_signal);

	mosquitto_lib_init();  //! mqtt初始化, 必須在使用其它函數之前呼叫

	memset(clientid, 0, 24);  // 先把clientid字串 都清為0, 避免有髒資料產生
	snprintf(clientid, 23, "mysql_log_%d\n", getpid());  
	mosq = mosquitto_new(clientid, true, 0);  

	if(mosq){
		mosquitto_connect_callback_set(mosq, connect_callback);  //todo 連線成功時, 呼叫connect_callback()函數
		mosquitto_message_callback_set(mosq, message_callback);  //todo 當從Broker收到訊息時, 呼叫message_callback()函數

	    rc = mosquitto_connect(mosq, mqtt_host, mqtt_port, 60);  // 跟MQTT Broker連線(結構指標變數, 主機IP, Port, 秒數) 參數4代表如果Broker在60秒內沒傳送任何封包的話, 就與Pi斷線

		mosquitto_subscribe(mosq, NULL, "fencel", 0);  // 跟Broker訂閱Topic(結構指標變數, 可以設定指標整數存放這則訊息, Topic, 此訂閱的QoS服務品質)

		while(run){   // run是靜態變數, 除非按下ctrl+c, run才會變0
			rc = mosquitto_loop(mosq, -1, 1);  //! mosquitto_loop(結構指標變數, -1代表等1000ms 0代表不等, 設1就好), 為了保持Broker和Client之間的溝通必須頻繁的呼叫
			if(run && rc){
				printf("跟Broker連線失敗!\n");
				sleep(10);
				mosquitto_reconnect(mosq);  // 重新連線
			}
		}
		mosquitto_destroy(mosq);  // 釋放client實例
	}

	mosquitto_lib_cleanup();  // 釋放相關的函式庫資源

	return rc;
}
